components.value:
height: 300px;
width: auto;
max-width: 100%;
object-fit: contain;

display: flex;
width: calc(100% - 64px);
justify-content: center;
flex-wrap: wrap;
gap: 16px;
padding: 32px;

display: inline-block;
height: 45px;
outline: 0;
border: 0;
cursor: pointer;
background-color: #4299e1;
border-radius: 4px;
padding: 8px 16px;
font-size: 16px;
border-bottom: 4px solid #2b6cb0;
font-weight: 700;
color: white;
line-height: 26px;

max-width: 250px;
height: 45px;
padding: 0 8px;
vertical-align: middle;
border-radius: 2px;
width: 100%;
min-height: 36px;
background-color: #ffffff;
border: 1px solid rgba(36,28,21,0.3);
transition: all 0.2s ease-in-out 0s;
font-size: 16px;
line-height: 18px;
font-weight: normal;

&:focus {
  outline: none;
  border: 1px solid #007c89;
  box-shadow: inset 0 0 0 1px #007c89;
}

display: flex;
justify-content: center;
align-items: center;
gap: 8px;
padding: 40px 15px;

